DROP TABLE IF EXISTS parkInfo;
CREATE TABLE parkInfo (park_id INT, colorRank INT, pool BOOLEAN, shade BOOLEAN, dogFriendly BOOLEAN, basketballCourt BOOLEAN, soccerField BOOLEAN, tennisCourt BOOLEAN, bikeFriendly BOOLEAN, skatePark BOOLEAN, playground BOOLEAN, create_date DATETIME, update_date DATETIME, PRIMARY KEY(park_id));
DROP TABLE IF EXISTS account;
CREATE TABLE account (user_id INT AUTO_INCREMENT, username VARCHAR(16), f_name VARCHAR(50), l_name VARCHAR(50), passwordHash CHAR(128), passwordSalt CHAR(16), create_date DATETIME, update_date DATETIME, PRIMARY KEY(user_id));
DROP TABLE IF EXISTS rating;
CREATE TABLE rating (park_id INT, createdBy INT, accessibility INT, atmosphere INT,noise INT,cleanliness INT,equipment INT,shade BOOLEAN,pet_friendliness INT, crowd INT, review VARCHAR(500), create_date DATETIME, update_date DATETIME, PRIMARY KEY(park_id, createdBy));
DROP TABLE IF EXISTS avgRating;
CREATE TABLE avgRating (park_id INT, accessibilityAVG INT, atmosphereAVG INT,noiseAVG INT,cleanlinessAVG INT,equipmentAVG INT, pet_friendlinessAVG INT, crowdAVG INT, PRIMARY KEY(park_id));
DROP TABLE IF EXISTS favorites;
CREATE TABLE favorites (park_id INT, user_id INT, create_date DATETIME, update_date DATETIME, PRIMARY KEY(park_id, user_id));
DROP TABLE IF EXISTS park;
CREATE TABLE park(park_id INT, park_name VARCHAR(100), park_supervisor_fname VARCHAR(50), park_supervisor_lname VARCHAR(50), address VARCHAR(50), city VARCHAR(50), state VARCHAR(20), zipcode CHAR(5), capacity INT, open_time TIME, close_time TIME, create_date DATETIME, update_date DATETIME, PRIMARY KEY(park_id));
DROP TABLE IF EXISTS incident;
CREATE TABLE incident (incident_id INT AUTO_INCREMENT, park_id INT, createdBy INT, incidentType INT, incidentDescription VARCHAR(200), incidentDate DATE, create_date DATETIME, update_date DATETIME, PRIMARY KEY(incident_id, park_id));
DROP TABLE IF EXISTS incidentTypes;
CREATE TABLE incidentTypes (incidentType INT, typeDescription VARCHAR(50), create_date DATETIME, update_date DATETIME, PRIMARY KEY (incidentType));
DROP TABLE IF EXISTS petAmenities;
CREATE TABLE petAmenities (park_id INT, doggie_bags BOOLEAN, water BOOLEAN, trashcans BOOLEAN, fenced_area BOOLEAN, leash_required BOOLEAN, create_date DATETIME, update_date DATETIME, PRIMARY KEY(park_id));
DROP TABLE IF EXISTS services;
CREATE TABLE services (park_id INT, police_id INT(3), care_id INT(3), fire_id INT(3), care_dist INT(3), police_dist INT(3), fire_dist INT(3), PRIMARY KEY(park_id));
DROP TABLE IF EXISTS policeStations;
CREATE TABLE policeStations (police_id INT, police_name VARCHAR(50), police_address VARCHAR(50), police_zip VARCHAR(5), police_phoneNum VARCHAR(12), PRIMARY KEY(police_id));
DROP TABLE IF EXISTS fireStations;
CREATE TABLE fireStations (fire_id INT, fire_stationNo INT, fire_address VARCHAR(50), fire_zip INT, PRIMARY KEY(fire_id));
DROP TABLE IF EXISTS careFacilities;
CREATE TABLE careFacilities (care_id INT, care_name VARCHAR(50),care_address VARCHAR(50), care_city VARCHAR(15), care_zipcode int(5), care_phoneNum VARCHAR(12), PRIMARY KEY(care_id));
DROP TABLE IF EXISTS sexOffenders;
CREATE TABLE sexOffenders (offender_id INT, f_name VARCHAR(50), l_name VARCHAR(50), street VARCHAR(50), city VARCHAR(50), state CHAR(2), zipcode INT(5), repeat_offender BOOLEAN, PRIMARY KEY(offender_id));
DROP TABLE IF EXISTS offenderLocation;
CREATE TABLE offenderLocation (park_id INT, offender_id INT, create_date DATETIME, update_date DATETIME);
